@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased text-foreground;
    background: var(--casino-dark);
    overflow-x: hidden;
  }

  /* Hide scrollbars globally */
  * {
    scrollbar-width: none; /* Firefox */
    -ms-overflow-style: none; /* IE and Edge */
  }

  /* Hide scrollbar for Chrome, Safari and Opera */
  *::-webkit-scrollbar {
    display: none;
  }
}

@layer components {
  .neon-border {
    box-shadow: 0 0 10px var(--arbitrum-blue), inset 0 0 10px rgba(40, 160, 240, 0.1);
    border-color: var(--arbitrum-blue);
  }

  .neon-border:hover {
    box-shadow: 0 0 20px var(--arbitrum-blue), inset 0 0 20px rgba(40, 160, 240, 0.2);
  }

  .neon-text {
    text-shadow: 0 0 10px var(--arbitrum-blue), 0 0 20px var(--arbitrum-blue);
  }

  .neon-green-text {
    text-shadow: 0 0 10px var(--neon-green), 0 0 20px var(--neon-green);
  }

  .neon-yellow-text {
    text-shadow: 0 0 10px var(--neon-yellow), 0 0 20px var(--neon-yellow);
  }

  .pulse-neon {
    animation: pulse-neon 2s infinite;
  }

  .glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .font-pixel {
    font-family: 'Press Start 2P', monospace;
  }

  .winning-glow {
    animation: winning-glow 1s ease-in-out infinite alternate;
  }
}

@keyframes pulse-neon {
  0%, 100% {
    box-shadow: 0 0 20px var(--arbitrum-blue), 0 0 40px var(--arbitrum-blue), 0 0 60px var(--arbitrum-blue);
    transform: scale(1);
  }
  50% {
    box-shadow: 0 0 30px var(--arbitrum-blue), 0 0 60px var(--arbitrum-blue), 0 0 90px var(--arbitrum-blue);
    transform: scale(1.05);
  }
}

@keyframes glow {
  from {
    text-shadow: 0 0 10px var(--arbitrum-blue), 0 0 20px var(--arbitrum-blue), 0 0 30px var(--arbitrum-blue);
  }
  to {
    text-shadow: 0 0 20px var(--arbitrum-blue), 0 0 30px var(--arbitrum-blue), 0 0 40px var(--arbitrum-blue);
  }
}

@keyframes winning-glow {
  from {
    text-shadow: 0 0 10px var(--neon-green), 0 0 20px var(--neon-green), 0 0 30px var(--neon-green);
  }
  to {
    text-shadow: 0 0 20px var(--neon-green), 0 0 30px var(--neon-green), 0 0 40px var(--neon-green);
  }
}

@keyframes spin-reel {
  0% { transform: translateY(0); }
  50% { transform: translateY(-200px); }
  100% { transform: translateY(0); }
}

@keyframes bounce-win {
  0% { transform: scale(1) rotate(0deg); }
  50% { transform: scale(1.2) rotate(5deg); }
  100% { transform: scale(1) rotate(0deg); }
}

.slot-container {
  overflow: hidden;
  height: 80px;
}

.meme-reel {
  position: relative;
  transform-style: preserve-3d;
  will-change: transform;
}

/* Classic arcade styling */
@keyframes arcade-glow {
  0%, 100% { 
    box-shadow: 0 0 10px rgba(255, 193, 7, 0.5);
  }
  50% { 
    box-shadow: 0 0 20px rgba(255, 193, 7, 0.8), 
                0 0 30px rgba(255, 193, 7, 0.4);
  }
}

.arcade-glow {
  animation: arcade-glow 2s ease-in-out infinite;
}

/* Meme coin bounce */
@keyframes meme-bounce {
  0%, 100% {
    transform: translateY(0px) scale(1);
  }
  50% {
    transform: translateY(-10px) scale(1.1);
  }
}

/* Slot machine gear rotation */
@keyframes gear-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.gear-spin {
  animation: gear-spin 2s linear infinite;
}

/* Slot handle pull effect */
@keyframes handle-pull {
  0% { transform: rotate(0deg); }
  50% { transform: rotate(-15deg); }
  100% { transform: rotate(0deg); }
}

.handle-pull {
  animation: handle-pull 0.3s ease-in-out;
}

/* Mobile responsive adjustments and premium features */
@media (max-width: 768px) {
  .font-pixel {
    font-size: 0.9rem;
  }

  /* Optimize touch targets for mobile */
  button, .slot-container {
    touch-action: manipulation;
    -webkit-tap-highlight-color: transparent;
  }

  /* Prevent text selection on mobile gestures */
  .select-none {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  /* Better mobile animations */
  .slot-container {
    transform: translateZ(0);
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
  }
}

/* Premium mobile animations */
@keyframes mobile-pulse {
  0%, 100% { 
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(59, 130, 246, 0.4);
  }
  50% { 
    transform: scale(1.05);
    box-shadow: 0 0 0 20px rgba(59, 130, 246, 0);
  }
}

.mobile-pulse {
  animation: mobile-pulse 2s infinite;
}

/* Touch-optimized hover states */
@media (hover: none) and (pointer: coarse) {
  .hover\:scale-105:hover {
    transform: scale(1.02);
  }
}

/* Hardware acceleration for smooth animations */
* {
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}

.slot-reel {
  transition: transform 2s ease-out;
}

.spin-animation {
  animation: spin-reel 2s ease-out;
}

.bounce-win {
  animation: bounce-win 0.6s ease-out;
}

/* Frame constraints for Farcaster Mini App */
.app-container {
  max-width: 390px;
  margin: 0 auto;
  min-height: 100vh;
}

