            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ address })
          })

          if (response.ok) {
            const userData = await response.json()
            setUser(userData)
            localStorage.setItem('farcaster_user', JSON.stringify(userData))
          } else {
            // Fallback if no Farcaster profile found
            const fallbackUser = {
              fid: parseInt(address.slice(-6), 16) % 100000 + 1000,
              username: `user-${address.slice(-4)}`,
              displayName: `Wallet User`,
              bio: 'Wallet connected but no Farcaster profile found',
              pfpUrl: `https://api.dicebear.com/7.x/avataaars/svg?seed=${address}`,
              custody: address,
              verifications: [address]
            }
            setUser(fallbackUser)
            localStorage.setItem('farcaster_user', JSON.stringify(fallbackUser))
          }
        } catch (apiError) {
          console.log('Failed to fetch from Farcaster API:', apiError)
          // Create fallback user data
          const fallbackUser = {
            fid: parseInt(address.slice(-6), 16) % 100000 + 1000,
            username: `user-${address.slice(-4)}`,
            displayName: `Wallet User`,
            bio: 'Connected wallet - Farcaster profile unavailable',
            pfpUrl: `https://api.dicebear.com/7.x/avataaars/svg?seed=${address}`,
            custody: address,
            verifications: [address]
          }
          setUser(fallbackUser)
          localStorage.setItem('farcaster_user', JSON.stringify(fallbackUser))
        }
      } catch (error) {
        console.log('Failed to fetch Farcaster user data:', error)
      } finally {
        setIsLoading(false)
      }
    }

    fetchUserData()
  }, [isConnected, address])

  const handleConnect = async () => {
    try {
      if (connectors[0]) {
        await connect({ connector: connectors[0] })
      }
    } catch (error) {
      console.error('Failed to connect wallet:', error)
    }
  }

  const handleDisconnect = () => {
    disconnect()
    setUser(null)
  }

  if (isConnected && user) {
    return (
      <Card className="bg-white/5 backdrop-blur-md border border-white/10">
        <CardHeader>
          <CardTitle className="text-white flex items-center gap-2">
            <LinkIcon className="w-5 h-5 text-green-400" />
            Farcaster Connected
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="flex items-center gap-4">
              {user.pfpUrl ? (
                <img 
                  src={user.pfpUrl} 
                  alt="Profile" 
                  className="w-16 h-16 rounded-full border-2 border-purple-400"
                />
              ) : (
                <div className="w-16 h-16 rounded-full bg-gradient-to-br from-purple-500 to-blue-500 flex items-center justify-center">
                  <User className="w-8 h-8 text-white" />
                </div>
              )}
              <div className="flex-1">
                <h3 className="text-white font-semibold text-lg">
                  {user.displayName || user.username || 'Farcaster User'}
                </h3>
                {user.username && (
                  <p className="text-white/60 text-sm">@{user.username}</p>
                )}
                <p className="text-white/50 text-xs">FID: {user.fid}</p>
                <p className="text-white/50 text-xs">Wallet: {address?.slice(0, 6)}...{address?.slice(-4)}</p>
                {user.bio && (
                  <p className="text-white/60 text-sm mt-2">{user.bio}</p>
                )}
              </div>
              <div className="flex flex-col gap-2">
                <Badge className="bg-green-500/20 text-green-400 border-green-400/30 text-xs">
                  Connected
                </Badge>
                <Badge className="bg-blue-500/20 text-blue-400 border-blue-400/30 text-xs">
                  Wallet Linked
                </Badge>
              </div>
            </div>
            <Button 
              onClick={handleDisconnect}
              variant="outline"
              size="sm"
              className="border-red-400/30 text-red-400 hover:bg-red-500/10"
            >
              Disconnect
            </Button>
          </div>
        </CardContent>
      </Card>
    )
  }

  return (
    <Card className="bg-white/5 backdrop-blur-md border border-white/10">
      <CardHeader>
        <CardTitle className="text-white flex items-center gap-2">
          <LinkIcon className="w-5 h-5" />
          Connect Farcaster Wallet
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4 text-center">
          <p className="text-white/60 text-sm">
            Connect your Farcaster wallet to access your profile data and start playing with real cryptocurrency rewards.
          </p>
          <Button 
            onClick={handleConnect}
            disabled={isPending || isLoading}
            className="bg-purple-600 hover:bg-purple-700 text-white w-full"
          >
            {isPending || isLoading ? 'Connecting...' : 'Connect Wallet'}
          </Button>
          {!isConnected && (
            <p className="text-white/40 text-xs">
              Your wallet and Farcaster profile will be automatically detected
            </p>
          )}
        </div>
      </CardContent>
    </Card>
  )
}

client/src/hooks/use-farcaster-auth.ts.content:
import { useState, useEffect } from 'react';
import { useAccount } from 'wagmi';

interface FarcasterUser {
  fid: number;
  username?: string;
  displayName?: string;
  bio?: string;
  pfpUrl?: string;
  custody?: string;
  verifications?: string[];
}

interface FarcasterAuth {
  user: FarcasterUser | null;
  isLoading: boolean;
  isAuthenticated: boolean;
  walletConnected: boolean;
  walletAddress?: string;
  authenticate: () => Promise<void>;
  signOut: () => void;
}

export function useFarcasterAuth(): FarcasterAuth {
  const { isConnected, address } = useAccount();
  const [user, setUser] = useState<FarcasterUser | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // This hook now primarily tracks Wagmi connection state
  // The actual user data fetching is handled by the FarcasterConnect component
  const authenticate = async () => {
    // This is now handled by the FarcasterConnect component
    // when the wallet is connected through Wagmi
    return Promise.resolve();
  };

  const signOut = () => {
    setUser(null);
    setIsAuthenticated(false);
    localStorage.removeItem('farcaster_user');
  };

  // Update authentication state based on wallet connection
  useEffect(() => {
    if (!isConnected) {
      setUser(null);
      setIsAuthenticated(false);
      return;
    }

    // Check for stored user data when wallet is connected
    const storedUser = localStorage.getItem('farcaster_user');
    if (storedUser) {
      try {
        const userData = JSON.parse(storedUser);
        setUser(userData);
        setIsAuthenticated(true);
      } catch (parseError) {
        console.error('Failed to parse stored user data:', parseError);
        localStorage.removeItem('farcaster_user');
      }
    }
  }, [isConnected]);

  return {
    user,
    isLoading,
    isAuthenticated: isAuthenticated && isConnected,
    walletConnected: isConnected,
    walletAddress: address,
    authenticate,
    signOut
  };
}

