import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { leaderboardService } from "./leaderboard";
import { insertSpinResultSchema, insertTokenSchema } from "@shared/schema";
import { ethers } from "ethers";
import { z } from "zod";
import { createFarcasterAuthMiddleware, verifyFarcasterToken, getUserByAddress } from "./farcaster";
import { blockchainService } from "./blockchain";
import { handleSpinResult } from "./spin-result-route";
import { registerSpinRoutes } from "./spin-routes";

// Routes without blockchain dependencies - will be configured fresh

export async function registerRoutes(app: Express): Promise<Server> {
  // Register new spin and claim routes
  registerSpinRoutes(app);
  // Get current user stats - optimized with parallel queries
  app.get("/api/user/:id", async (req, res) => {
    try {
      // Run queries in parallel for better performance
      const [user, spinsToday] = await Promise.all([
        storage.getUser(req.params.id),
        storage.getUserSpinsToday(req.params.id)
      ]);

      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      res.json({ ...user, spinsUsed: spinsToday });
    } catch (error) {
      console.error('Get user error:', error);
      res.status(500).json({ error: "Failed to get user" });
    }
  });

  // Create or get user by username
  app.post("/api/user", async (req, res) => {
    try {
      const { username, walletAddress } = req.body;

      let user = await storage.getUserByUsername(username);
      if (!user) {
        user = await storage.createUser({ username, walletAddress });
      }

      const spinsToday = await storage.getUserSpinsToday(user.id);
      res.json({ ...user, spinsUsed: spinsToday });
    } catch (error) {
      res.status(500).json({ error: "Failed to create/get user" });
    }
  });

  // Perform spin
  app.post("/api/spin", async (req, res) => {
    try {
      const { userId, userAddress } = req.body;

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      const spinsToday = await storage.getUserSpinsToday(userId);
      if (spinsToday >= 3) {
        return res.status(400).json({ error: "Daily spin limit reached" });
      }

      // Blockchain integration will be implemented when contracts are ready
      console.log(`ðŸŽ° Spin request for user ${userId} - blockchain integration pending`);

      // For now, return a clear message until contracts are deployed
      res.status(503).json({ 
        error: "Blockchain integration not yet configured - contracts need to be deployed first",
        message: "Please deploy contracts first to enable spinning functionality"
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to perform spin" });
    }
  });

  // Parse user's transaction for spin result (user pays gas fees)
  app.post("/api/spin-result", handleSpinResult);

  // Get game statistics
  app.get("/api/stats", async (req, res) => {
    try {
      const stats = await storage.getGameStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ error: "Failed to get stats" });
    }
  });

  // Contract events-based leaderboard - optimized with caching
  const leaderboardCache = new Map<string, { data: any; timestamp: number }>();
  const CACHE_TTL = 30 * 1000; // 30 seconds cache

  app.get("/api/leaderboard", async (req, res) => {
    const { category = 'wins', limit = 10 } = req.query;
    const cacheKey = `${category}-${limit}`;

    try {
      // Check cache first
      const cached = leaderboardCache.get(cacheKey);
      if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
        return res.json(cached.data);
      }

      // Sync latest data from contract (non-blocking)
      leaderboardService.syncLeaderboardData().catch(console.error);

      const leaderboard = await leaderboardService.getLeaderboard(
        category as 'wins' | 'spins' | 'rewards',
        parseInt(limit as string) || 10
      );

      // Cache the result
      leaderboardCache.set(cacheKey, { data: leaderboard, timestamp: Date.now() });

      res.json(leaderboard);
    } catch (error) {
      console.error("Leaderboard error:", error);
      res.status(500).json({ error: "Failed to fetch leaderboard" });
    }
  });

  app.get("/api/leaderboard/weekly", async (req, res) => {
    const { limit = 10 } = req.query;

    try {
      const weeklyLeaderboard = await leaderboardService.getWeeklyLeaderboard(
        parseInt(limit as string) || 10
      );

      res.json(weeklyLeaderboard);
    } catch (error) {
      console.error("Weekly leaderboard error:", error);
      res.status(500).json({ error: "Failed to fetch weekly leaderboard" });
    }
  });

  app.get("/api/player/:address/rank", async (req, res) => {
    const { address } = req.params;
    const { category = 'wins' } = req.query;

    try {
      const playerRank = await leaderboardService.getPlayerRank(
        address,
        category as 'wins' | 'spins' | 'rewards'
      );

      if (!playerRank) {
        return res.status(404).json({ error: "Player not found" });
      }

      res.json(playerRank);
    } catch (error) {
      console.error("Player rank error:", error);
      res.status(500).json({ error: "Failed to fetch player rank" });
    }
  });

  // Get user's accumulated token balances - optimized with parallel queries
  app.get("/api/user/:id/balances", async (req, res) => {
    try {
      // Run balance and claim queries in parallel
      const [balances, claimInfo] = await Promise.all([
        storage.getUserAccumulatedBalances(req.params.id),
        storage.canUserClaim(req.params.id)
      ]);

      res.json({ ...balances, ...claimInfo });
    } catch (error) {
      console.error('Get balances error:', error);
      res.status(500).json({ error: "Failed to get user balances" });
    }
  });

  // Claim accumulated tokens
  app.post("/api/claim", async (req, res) => {
    try {
      const { userId } = req.body;

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      if (!user.walletAddress) {
        return res.status(400).json({ error: "Wallet address required for claiming" });
      }

      const claimInfo = await storage.canUserClaim(userId);

      // For testing: allow claims even if threshold not met
      console.log("ðŸ§ª TESTING MODE - Force claim: Bypassing minimum threshold requirements");

      const balances = await storage.getUserAccumulatedBalances(userId);

      // Token addresses will be configured when contracts are deployed
      const tokenAddresses = await blockchainService.getTokenAddresses();

      let transactionHash = null;
      let claimStatus = "pending";

      try {
        // For now, we'll simulate the transfer and just record the claim
        // In production, you'd batch transfer all tokens in a single transaction
        console.log(`ðŸš€ Claiming tokens for user ${userId}:`);
        console.log(`  TOKEN1: ${balances.token1}`);
        console.log(`  TOKEN2: ${balances.token2}`);
        console.log(`  TOKEN3: ${balances.token3}`);
        console.log(`  Total Value: $${claimInfo.totalValueUSD}`);

        // Create the claim record
        const tokenClaim = await storage.createTokenClaim({
          userId,
          token1Amount: balances.token1,
          token2Amount: balances.token2,
          token3Amount: balances.token3,
          totalValueUSD: claimInfo.totalValueUSD,
          transactionHash: null, // Will be updated when transaction is confirmed
          status: "pending"
        });

        // Reset user's accumulated balances and update claimed totals
        await storage.updateUser(userId, {
          accumulatedToken1: "0",
          accumulatedToken2: "0", 
          accumulatedToken3: "0",
          claimedToken1: (BigInt(user.claimedToken1 || '0') + BigInt(balances.token1)).toString(),
          claimedToken2: (BigInt(user.claimedToken2 || '0') + BigInt(balances.token2)).toString(),
          claimedToken3: (BigInt(user.claimedToken3 || '0') + BigInt(balances.token3)).toString(),
          lastClaimDate: new Date()
        });

        res.json({ 
          success: true, 
          claim: tokenClaim,
          message: "Tokens claimed successfully! They will be transferred to your wallet soon."
        });

      } catch (error) {
        console.error("âŒ Claim failed:", error);
        res.status(500).json({ error: "Failed to process claim" });
      }
    } catch (error) {
      res.status(500).json({ error: "Failed to claim tokens" });
    }
  });

  // Get user's claim history
  app.get("/api/user/:id/claims", async (req, res) => {
    try {
      const claims = await storage.getUserClaims(req.params.id);
      res.json(claims);
    } catch (error) {
      res.status(500).json({ error: "Failed to get user claims" });
    }
  });

  // Token management routes
  app.get("/api/tokens", async (req, res) => {
    try {
      const tokens = await storage.getTokens();
      res.json(tokens);
    } catch (error) {
      res.status(500).json({ error: "Failed to get tokens" });
    }
  });

  app.get("/api/tokens/active", async (req, res) => {
    try {
      const activeTokens = await storage.getActiveTokens();
      res.json(activeTokens);
    } catch (error) {
      res.status(500).json({ error: "Failed to get active tokens" });
    }
  });

  app.post("/api/tokens", async (req, res) => {
    try {
      const tokenData = insertTokenSchema.parse(req.body);
      const token = await storage.createToken(tokenData);
      res.json(token);
    } catch (error) {
      res.status(500).json({ error: "Failed to create token" });
    }
  });

  app.put("/api/tokens/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const token = await storage.updateToken(id, updates);
      if (!token) {
        return res.status(404).json({ error: "Token not found" });
      }
      res.json(token);
    } catch (error) {
      res.status(500).json({ error: "Failed to update token" });
    }
  });

  // Farcaster authentication endpoint
  app.get("/api/farcaster/me", async (req, res) => {
    try {
      const authorization = req.headers.authorization;

      if (!authorization || !authorization.startsWith('Bearer ')) {
        return res.status(401).json({ error: 'Missing or invalid authorization header' });
      }

      const token = authorization.split(' ')[1];
      const domain = req.headers.host || 'localhost';

      const farcasterUser = await verifyFarcasterToken(token, domain);

      res.json(farcasterUser);
    } catch (error) {
      console.error('Farcaster auth error:', error);
      res.status(401).json({ error: 'Invalid Farcaster authentication' });
    }
  });

  // Test endpoint to verify Hub API is working
  app.get("/api/farcaster/test/:fid", async (req, res) => {
    try {
      const fid = parseInt(req.params.fid);

      if (isNaN(fid)) {
        return res.status(400).json({ error: "Invalid FID" });
      }

      console.log(`ðŸ§ª Testing Hub API with FID: ${fid}`);

      const response = await fetch(`https://hub.pinata.cloud/v1/userDataByFid?fid=${fid}`);

      if (!response.ok) {
        return res.status(response.status).json({ error: `Hub API returned ${response.status}` });
      }

      const data = await response.json();
      console.log(`ðŸ“¦ Raw Hub API response for FID ${fid}:`, JSON.stringify(data, null, 2));

      res.json(data);
    } catch (error) {
      console.error('Hub API test error:', error);
      res.status(500).json({ error: "Failed to test Hub API" });
    }
  });

  // Get Farcaster user by Ethereum address
  app.post("/api/farcaster/user-by-address", async (req, res) => {
    try {
      const { address } = req.body;

      if (!address) {
        return res.status(400).json({ error: 'Address is required' });
      }

      const farcasterUser = await getUserByAddress(address);

      if (farcasterUser) {
        res.json(farcasterUser);
      } else {
        res.status(404).json({ error: 'No Farcaster profile found for this address' });
      }
    } catch (error) {
      console.error('Error fetching user by address:', error);
      res.status(500).json({ error: 'Failed to fetch user data' });
    }
  });

  // Configuration endpoint for frontend
  app.get("/api/config", async (req, res) => {
    try {
      const contractAddress = await blockchainService.getContractAddress();
      const tokenAddresses = await blockchainService.getTokenAddresses();
      const chainId = await blockchainService.getChainId();

      res.json({
        contractAddress,
        tokenAddresses,
        chainId
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to get configuration" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type GameStats, type InsertGameStats, type SpinResult, type InsertSpinResult, type Token, type InsertToken, type TokenClaim, type InsertTokenClaim, users, gameStats, spinResults, tokens, tokenClaims } from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserById(id: string): Promise<User | undefined>; // Alias for getUser
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User | undefined>;
  getGameStats(): Promise<GameStats>;
  updateGameStats(updates: Partial<GameStats>): Promise<GameStats>;
  createSpinResult(result: InsertSpinResult): Promise<SpinResult>;
  addSpinResult(result: InsertSpinResult): Promise<SpinResult>; // Alias for createSpinResult
  getLeaderboard(): Promise<User[]>;
  getUserSpinsToday(userId: string): Promise<number>;
  getTokens(): Promise<Token[]>;
  createToken(token: InsertToken): Promise<Token>;
  updateToken(id: string, updates: Partial<Token>): Promise<Token | undefined>;
  getActiveTokens(): Promise<Token[]>;
  // New methods for accumulated rewards
  addAccumulatedReward(userId: string, tokenType: string, amount: string): Promise<User | undefined>;
  getUserAccumulatedBalances(userId: string): Promise<{ token1: string; token2: string; token3: string }>;
  createTokenClaim(claim: InsertTokenClaim): Promise<TokenClaim>;
  addTokenClaim(claim: InsertTokenClaim): Promise<TokenClaim>; // Alias for createTokenClaim
  getUserClaims(userId: string): Promise<TokenClaim[]>;
  canUserClaim(userId: string): Promise<{ canClaim: boolean; totalValueUSD: string }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserById(id: string): Promise<User | undefined> {
    return this.getUser(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set(updates)
      .where(eq(users.id, id))
      .returning();
    return user || undefined;
  }

  async getGameStats(): Promise<GameStats> {
    const [stats] = await db.select().from(gameStats).limit(1);
    if (!stats) {
      // Create initial stats if none exist
      const [newStats] = await db
        .insert(gameStats)
        .values({ totalClaims: 1024, contractTxs: 839 })
        .returning();
      return newStats;
    }
    return stats;
  }

  async updateGameStats(updates: Partial<GameStats>): Promise<GameStats> {
    const currentStats = await this.getGameStats();
    const [updatedStats] = await db
      .update(gameStats)
      .set(updates)
      .where(eq(gameStats.id, currentStats.id))
      .returning();
    return updatedStats;
  }

  async createSpinResult(result: InsertSpinResult): Promise<SpinResult> {
    const [spinResult] = await db
      .insert(spinResults)
      .values(result)
      .returning();
    return spinResult;
  }

  async addSpinResult(result: InsertSpinResult): Promise<SpinResult> {
    return this.createSpinResult(result);
  }

  async getLeaderboard(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .orderBy(desc(users.totalWins))
      .limit(10);
  }

  async getUserSpinsToday(userId: string): Promise<number> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const result = await db
      .select({ count: sql<number>`count(*)` })
      .from(spinResults)
      .where(
        sql`${spinResults.userId} = ${userId} AND ${spinResults.timestamp} >= ${today}`
      );

    return result[0]?.count || 0;
  }

  async getTokens(): Promise<Token[]> {
    return await db.select().from(tokens);
  }

  async createToken(token: InsertToken): Promise<Token> {
    const [newToken] = await db
      .insert(tokens)
      .values(token)
      .returning();
    return newToken;
  }

  async updateToken(id: string, updates: Partial<Token>): Promise<Token | undefined> {
    const [token] = await db
      .update(tokens)
      .set(updates)
      .where(eq(tokens.id, id))
      .returning();
    return token || undefined;
  }

  async getActiveTokens(): Promise<Token[]> {
    return await db
      .select()
      .from(tokens)
      .where(eq(tokens.isActive, true));
  }

  // New methods for accumulated rewards
  async addAccumulatedReward(userId: string, tokenType: string, amount: string): Promise<User | undefined> {
    const user = await this.getUser(userId);
    if (!user) return undefined;

    let updates: Partial<User> = {};

    switch (tokenType) {
      case 'TOKEN1':
        const newToken1 = (BigInt(user.accumulatedToken1 || '0') + BigInt(amount)).toString();
        updates.accumulatedToken1 = newToken1;
        break;
      case 'TOKEN2':
        const newToken2 = (BigInt(user.accumulatedToken2 || '0') + BigInt(amount)).toString();
        updates.accumulatedToken2 = newToken2;
        break;
      case 'TOKEN3':
        const newToken3 = (BigInt(user.accumulatedToken3 || '0') + BigInt(amount)).toString();
        updates.accumulatedToken3 = newToken3;
        break;
      default:
        return user;
    }

    return await this.updateUser(userId, updates);
  }

  async getUserAccumulatedBalances(userId: string): Promise<{ token1: string; token2: string; token3: string }> {
    const user = await this.getUser(userId);
    if (!user) {
      return { token1: '0', token2: '0', token3: '0' };
    }

    return {
      token1: user.accumulatedToken1 || '0',
      token2: user.accumulatedToken2 || '0',
      token3: user.accumulatedToken3 || '0'
    };
  }

  async createTokenClaim(claim: InsertTokenClaim): Promise<TokenClaim> {
    const [newClaim] = await db
      .insert(tokenClaims)
      .values(claim)
      .returning();
    return newClaim;
  }

  async addTokenClaim(claim: InsertTokenClaim): Promise<TokenClaim> {
    return this.createTokenClaim(claim);
  }

  async getUserClaims(userId: string): Promise<TokenClaim[]> {
    return await db
      .select()
      .from(tokenClaims)
      .where(eq(tokenClaims.userId, userId))
      .orderBy(desc(tokenClaims.timestamp));
  }

  async canUserClaim(userId: string): Promise<{ canClaim: boolean; totalValueUSD: string }> {
    const balances = await this.getUserAccumulatedBalances(userId);

    // Simple calculation: assume each token is worth $0.0001 USD for demo purposes
    // In reality, you'd fetch real prices from a price oracle
    const token1ValueUSD = (BigInt(balances.token1) * BigInt(1)) / BigInt(10000); // $0.0001 per token
    const token2ValueUSD = (BigInt(balances.token2) * BigInt(2)) / BigInt(10000); // $0.0002 per token  
    const token3ValueUSD = (BigInt(balances.token3) * BigInt(5)) / BigInt(10000); // $0.0005 per token

    const totalValueUSD = (token1ValueUSD + token2ValueUSD + token3ValueUSD).toString();
    const minClaimThreshold = BigInt(100); // $1.00 minimum claim threshold

    return {
      canClaim: (token1ValueUSD + token2ValueUSD + token3ValueUSD) >= minClaimThreshold,
      totalValueUSD
    };
  }
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private gameStats: GameStats;
  private spinResults: Map<string, SpinResult>;
  private tokens: Map<string, Token>;

  constructor() {
    this.users = new Map();
    this.gameStats = {
      id: randomUUID(),
      date: new Date(),
      totalClaims: 1024,
      contractTxs: 839,
    };
    this.spinResults = new Map();
    this.tokens = new Map();

    // Initialize with some mock users for leaderboard and tokens
    this.initializeMockData();
    this.initializeTokens();
  }

  private initializeMockData() {
    const generateMockAddress = () => `0x${Array(40).fill(0).map(() => Math.floor(Math.random() * 16).toString(16)).join('')}`;

    const mockUsers = [
      { username: "0xAb...C1F", walletAddress: generateMockAddress(), spinsUsed: 2, totalWins: 4, totalSpins: 4 },
      { username: "0xD2...B12", walletAddress: generateMockAddress(), spinsUsed: 2, totalWins: 3, totalSpins: 5 },
      { username: "0xE3...A45", walletAddress: generateMockAddress(), spinsUsed: 1, totalWins: 2, totalSpins: 3 },
      { username: "0xF4...B67", walletAddress: generateMockAddress(), spinsUsed: 2, totalWins: 2, totalSpins: 4 },
      { username: "0xG5...C89", walletAddress: generateMockAddress(), spinsUsed: 1, totalWins: 1, totalSpins: 2 },
    ];

    mockUsers.forEach(user => {
      const id = randomUUID();
      const fullUser: User = {
        id,
        ...user,
        farcasterFid: null,
        farcasterUsername: null,
        farcasterDisplayName: null,
        farcasterPfpUrl: null,
        farcasterBio: null,
        accumulatedToken1: "0",
        accumulatedToken2: "0",
        accumulatedToken3: "0",
        claimedToken1: "0",
        claimedToken2: "0",
        claimedToken3: "0",
        lastClaimDate: null,
        lastSpinDate: new Date(),
        createdAt: new Date(),
      };
      this.users.set(id, fullUser);
    });
  }

  private initializeTokens() {
    const tokensData = [
      {
        address: "0x09e18590e8f76b6cf471b3cd75fe1a1a9d2b2c2b",
        symbol: "TOKEN1",
        name: "First Token",
        decimals: 18,
        isActive: true,
        rewardAmount: 50000000000000 // 0.00005 tokens (very small amount)
      },
      {
        address: "0x13a7dedb7169a17be92b0e3c7c2315b46f4772b3",
        symbol: "TOKEN2", 
        name: "Second Token",
        decimals: 18,
        isActive: true,
        rewardAmount: 100000000000000 // 0.0001 tokens
      },
      {
        address: "0xbc4c97fb9befaa8b41448e1dfcc5236da543217f",
        symbol: "TOKEN3",
        name: "Third Token", 
        decimals: 18,
        isActive: true,
        rewardAmount: 25000000000000 // 0.000025 tokens
      }
    ];

    tokensData.forEach(tokenData => {
      const id = randomUUID();
      const token: Token = {
        id,
        ...tokenData,
      };
      this.tokens.set(id, token);
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser,
      walletAddress: insertUser.walletAddress || null,
      id,
      farcasterFid: insertUser.farcasterFid || null,
      farcasterUsername: insertUser.farcasterUsername || null,
      farcasterDisplayName: insertUser.farcasterDisplayName || null,
      farcasterPfpUrl: insertUser.farcasterPfpUrl || null,
      farcasterBio: insertUser.farcasterBio || null,
      spinsUsed: 0,
      totalWins: 0,
      totalSpins: 0,
      accumulatedToken1: "0",
      accumulatedToken2: "0",
      accumulatedToken3: "0",
      claimedToken1: "0",
      claimedToken2: "0",
      claimedToken3: "0",
      lastSpinDate: null,
      lastClaimDate: null,
      createdAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getGameStats(): Promise<GameStats> {
    return this.gameStats;
  }

  async updateGameStats(updates: Partial<GameStats>): Promise<GameStats> {
    this.gameStats = { ...this.gameStats, ...updates };
    return this.gameStats;
  }

  async createSpinResult(result: InsertSpinResult): Promise<SpinResult> {
    const id = randomUUID();
    const spinResult: SpinResult = {
      ...result,
      rewardAmount: result.rewardAmount || "0",
      userId: result.userId || null,
      symbols: result.symbols || null,
      isWin: result.isWin || false,
      tokenType: result.tokenType || null,
      tokenId: result.tokenId || null,
      tokenAddress: result.tokenAddress || null,
      isAccumulated: result.isAccumulated !== undefined ? result.isAccumulated : true,
      claimType: result.claimType || null,
      transactionHash: result.transactionHash || null,
      id,
      timestamp: new Date(),
    };
    this.spinResults.set(id, spinResult);
    return spinResult;
  }

  async getUserById(id: string): Promise<User | undefined> {
    return this.getUser(id);
  }

  async addSpinResult(result: InsertSpinResult): Promise<SpinResult> {
    return this.createSpinResult(result);
  }

  async getLeaderboard(): Promise<User[]> {
    return Array.from(this.users.values())
      .sort((a, b) => (b.totalWins || 0) - (a.totalWins || 0))
      .slice(0, 10);
  }

  async getUserSpinsToday(userId: string): Promise<number> {
    const user = this.users.get(userId);
    if (!user || !user.lastSpinDate) return 0;

    const today = new Date();
    const lastSpin = new Date(user.lastSpinDate);

    // Check if last spin was today (UTC)
    if (
      today.getUTCDate() === lastSpin.getUTCDate() &&
      today.getUTCMonth() === lastSpin.getUTCMonth() &&
      today.getUTCFullYear() === lastSpin.getUTCFullYear()
    ) {
      return user.spinsUsed || 0;
    }

    return 0;
  }

  async getTokens(): Promise<Token[]> {
    return Array.from(this.tokens.values());
  }

  async createToken(insertToken: InsertToken): Promise<Token> {
    const id = randomUUID();
    const token: Token = {
      ...insertToken,
      decimals: insertToken.decimals || 18,
      isActive: insertToken.isActive !== undefined ? insertToken.isActive : true,
      rewardAmount: insertToken.rewardAmount || 100,
      id,
    };
    this.tokens.set(id, token);
    return token;
  }

  async updateToken(id: string, updates: Partial<Token>): Promise<Token | undefined> {
    const token = this.tokens.get(id);
    if (!token) return undefined;

    const updatedToken = { ...token, ...updates };
    this.tokens.set(id, updatedToken);
    return updatedToken;
  }

  async getActiveTokens(): Promise<Token[]> {
    return Array.from(this.tokens.values()).filter(token => token.isActive);
  }

  // New methods for accumulated rewards (placeholder implementations)
  async addAccumulatedReward(userId: string, tokenType: string, amount: string): Promise<User | undefined> {
    // For memory storage, we'll just return the user - this is a placeholder
    return this.users.get(userId);
  }

  async getUserAccumulatedBalances(userId: string): Promise<{ token1: string; token2: string; token3: string }> {
    return { token1: '0', token2: '0', token3: '0' };
  }

  async createTokenClaim(claim: InsertTokenClaim): Promise<TokenClaim> {
    const id = randomUUID();
    const tokenClaim: TokenClaim = {
      ...claim,
      userId: claim.userId || null,
      token1Amount: claim.token1Amount || "0",
      token2Amount: claim.token2Amount || "0",
      token3Amount: claim.token3Amount || "0",
      totalValueUSD: claim.totalValueUSD || "0",
      transactionHash: claim.transactionHash || null,
      status: claim.status || "pending",
      id,
      timestamp: new Date(),
    };
    return tokenClaim;
  }

  async addTokenClaim(claim: InsertTokenClaim): Promise<TokenClaim> {
    return this.createTokenClaim(claim);
  }

  async getUserClaims(userId: string): Promise<TokenClaim[]> {
    return [];
  }

  async canUserClaim(userId: string): Promise<{ canClaim: boolean; totalValueUSD: string }> {
    return { canClaim: false, totalValueUSD: '0' };
  }
}

