@layer base {
  * {
    /* Hardware acceleration for smooth scrolling */
    box-sizing: border-box;
  }

  /* Enable scrolling with hidden scrollbars */
  html, body {
    overflow-x: hidden;
    overflow-y: auto;
    height: 100vh;
    width: 100vw;
    margin: 0;
    padding: 0;
    scrollbar-width: none; /* Firefox */
    -ms-overflow-style: none; /* IE and Edge */
    /* Mobile browser compatibility */
    -webkit-user-select: none;
    -webkit-touch-callout: none;
    -webkit-tap-highlight-color: transparent;
    caret-color: transparent;
  }

  /* Ensure root element uses full width */
  #root {
    width: 100vw;
    min-height: 100vh;
    margin: 0;
    padding: 0;
    overflow-x: hidden;
    border: none;
    outline: none;
  }

  /* Remove any default browser borders and ensure full width */
  body, html, #root, * {
    border: none !important;
    outline: none !important;
  }

  /* Specific Farcaster mini app full-width styles */
  body {
    position: relative;
  }

  /* Override any container or tailwind border classes */
  .rounded-xl, .rounded-2xl, .rounded-3xl, .rounded-full {
    border: none !important;
  }

  html::-webkit-scrollbar,
  body::-webkit-scrollbar {
    display: none; /* Chrome, Safari, Opera */
  }

  /* Global performance optimizations */
  *,
  *::before,
  *::after {
    box-sizing: border-box;
    caret-color: transparent;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  /* Enable GPU acceleration for transforms */
  .will-change-transform {
    will-change: transform;
    transform: translateZ(0);
  }

  .will-change-scroll {
