  chains: [arbitrumSepolia, arbitrum], // Support both testnet and mainnet
  connectors: [
    miniAppConnector(), // Farcaster wallet integration
    injected(), // MetaMask and other injected wallets
    coinbaseWallet({
      appName: 'ArbCasino',
    }),
