import { motion } from "framer-motion";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Coins, Zap, Award, Clock, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { formatUnits } from "ethers";

interface User {
  id: string;
  username: string;
  walletAddress?: string;
  farcasterFid?: number;
  farcasterUsername?: string;
  farcasterDisplayName?: string;
  farcasterPfpUrl?: string;
  spinsUsed: number;
  totalWins: number;
  totalSpins: number;
  lastSpinDate?: string;
}

interface TokenBalances {
  token1: string;
  token2: string;
  token3: string;
  canClaim: boolean;
  totalValueUSD: string;
}

interface TokenClaim {
  id: string;
  userId: string;
  token1Amount: string;
  token2Amount: string;
  token3Amount: string;
  totalValueUSD: string;
  transactionHash: string | null;
  status: string;
  timestamp: string;
}

export default function TokenCollection() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get user data
  const { data: user, isLoading: userLoading } = useQuery<User>({
    queryKey: ['/api/user'],
  });

  // Get token balances
  const { data: balances, isLoading: balancesLoading } = useQuery<TokenBalances>({
    queryKey: ['/api/user', user?.id, 'balances'],
    enabled: !!user?.id,
  });

  // Get recent claims
  const { data: claims } = useQuery<TokenClaim[]>({
    queryKey: ['/api/user', user?.id, 'claims'],
    enabled: !!user?.id,
  });

  const claimMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/claim', {
        method: 'POST',
        body: JSON.stringify({ userId: user?.id }),
        headers: { 'Content-Type': 'application/json' }
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to claim tokens');
      }

      return response.json();
    },
    onSuccess: (data: any) => {
      toast({
        title: "Tokens Claimed!",
        description: data.message || "Tokens claimed successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/user', user?.id, 'balances'] });
      queryClient.invalidateQueries({ queryKey: ['/api/user', user?.id, 'claims'] });
    },
    onError: (error: Error) => {
      toast({
        title: "Claim Failed",
        description: error.message || "Failed to claim tokens",
        variant: "destructive",
      });
    },
  });

  const formatTokenAmount = (amount: string, decimals = 18) => {
    try {
      const parsed = parseFloat(formatUnits(amount, decimals));
      if (parsed >= 1000) {
        return `+${(parsed / 1000).toFixed(1)}K`;
      }
      return `+${parsed.toFixed(0)}`;
    } catch {
      return "0";
    }
  };

  const formatTimeAgo = (timestamp: string) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffInMinutes = Math.floor((now.getTime() - time.getTime()) / (1000 * 60));

    if (diffInMinutes < 60) {
      return `${diffInMinutes}m`;
    }

    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) {
      return `${diffInHours}h ${diffInMinutes % 60}m`;
    }

    const diffInDays = Math.floor(diffInHours / 24);
    return `${diffInDays}d ${diffInHours % 24}h ${diffInMinutes % 60}m`;
  };

  const tokenData = [
    {
      symbol: "AIDOGE",
      name: "AiDoge",
      amount: balances?.token1 || "0",
      icon: Coins,
      color: "text-yellow-400",
      bgColor: "bg-yellow-400/10",
    },
    {
      symbol: "BOOP", 
      name: "Boop",
      amount: balances?.token2 || "0",
      icon: Zap,
      color: "text-blue-400",
      bgColor: "bg-blue-400/10",
    },
    {
      symbol: "CATCH",
      name: "Catch",
      amount: balances?.token3 || "0",
      icon: Award,
      color: "text-purple-400",
      bgColor: "bg-purple-400/10",
    },
  ];

  const hasTokens = tokenData.some(token => BigInt(token.amount) > 0);
  const canClaim = balances?.canClaim || false;

  if (userLoading || balancesLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-900 via-gray-800 to-black p-4">
        <div className="max-w-md mx-auto">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-700 rounded"></div>
            <div className="space-y-3">
              {[1, 2, 3].map((i) => (
                <div key={i} className="h-20 bg-gray-700 rounded-lg"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 via-gray-800 to-black">
      <div className="max-w-md mx-auto p-4 space-y-6">

        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center pt-8 pb-4"
        >
          <h1 className="text-2xl font-bold text-white mb-2">
            <Coins className="inline-block w-6 h-6 mr-2 text-yellow-400" />
            Token Collection
          </h1>
          <p className="text-gray-400 text-sm">Manage your earned tokens</p>
        </motion.div>

        {/* Token List */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="space-y-3"
        >
          {tokenData.map((token, index) => {
            const Icon = token.icon;
            const formattedAmount = formatTokenAmount(token.amount);
            const hasBalance = BigInt(token.amount) > 0;
            const recentClaim = claims?.find(claim => 
              (token.symbol === "AIDOGE" && BigInt(claim.token1Amount || "0") > 0) ||
              (token.symbol === "BOOP" && BigInt(claim.token2Amount || "0") > 0) ||
              (token.symbol === "CATCH" && BigInt(claim.token3Amount || "0") > 0)
            );

            return (
              <motion.div
                key={token.symbol}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.3 + index * 0.1 }}
                className="bg-gray-800/80 backdrop-blur-sm rounded-xl p-4 border border-gray-700/50"
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <div className={`p-2 rounded-full ${token.bgColor}`}>
                      <Icon className={`h-5 w-5 ${token.color}`} />
                    </div>
                    <div>
                      <div className="font-semibold text-white text-base">
                        {token.symbol}
                      </div>
                      <div className="flex items-center gap-2 text-xs text-gray-400">
                        <Clock className="h-3 w-3" />
                        {recentClaim ? formatTimeAgo(recentClaim.timestamp) : "Never"}
                      </div>
                    </div>
                  </div>

                  <div className="text-right">
                    <div className={`text-lg font-bold ${hasBalance ? 'text-green-400' : 'text-gray-500'}`}>
                      {formattedAmount}
                    </div>
                  </div>
                </div>

                {/* Individual token claim button */}
                {hasBalance && (
                  <motion.div 
                    className="mt-3 pt-3 border-t border-gray-700/50"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.5 + index * 0.1 }}
                  >
                    <Button
                      onClick={() => claimMutation.mutate()}
                      disabled={!hasBalance || claimMutation.isPending}
                      size="sm"
                      className={`w-full ${token.bgColor} ${token.color} border border-gray-600/50 hover:border-gray-500/50 bg-gray-700/50 hover:bg-gray-600/50`}
                    >
                      {claimMutation.isPending ? (
                        "Processing..."
                      ) : (
                        "Claim"
                      )}
                    </Button>
                  </motion.div>
                )}
              </motion.div>
            );
          })}
        </motion.div>

        {/* Global Claim Button */}
        {hasTokens && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.8 }}
            className="pt-4"
          >
            <Button
              onClick={() => claimMutation.mutate()}
              disabled={!canClaim || claimMutation.isPending}
              className={`w-full py-3 ${
                canClaim 
                  ? 'bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700' 
                  : 'bg-gray-700 cursor-not-allowed'
              } text-white transition-all duration-200`}
            >
              {claimMutation.isPending ? (
                "Processing..."
              ) : canClaim ? (
                <>
                  <CheckCircle className="w-4 h-4 mr-2" />
                  Claim All Tokens
                </>
              ) : (
                "Minimum $1.00 required"
              )}
            </Button>
          </motion.div>
        )}

        {/* No tokens message */}
        {!hasTokens && !balancesLoading && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.5 }}
            className="text-center py-12"
          >
            <Coins className="w-16 h-16 mx-auto mb-4 text-gray-600" />
            <h3 className="text-lg font-semibold text-gray-400 mb-2">No Tokens Yet</h3>
            <p className="text-gray-500 text-sm">
              Start spinning to earn tokens and build your collection!
            </p>
          </motion.div>
        )}

        {/* Recent Claims History */}
        {claims && claims.length > 0 && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.0 }}
          >
            <Card className="bg-gray-900/80 border-gray-700 backdrop-blur-sm">
              <CardHeader className="pb-3">
                <CardTitle className="text-white text-sm flex items-center gap-2">
                  <CheckCircle className="h-4 w-4 text-green-400" />
                  Recent Claims
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {claims.slice(0, 3).map((claim) => (
                  <div
                    key={claim.id}
                    className="flex justify-between items-center p-2 bg-gray-800/50 rounded-lg"
                  >
                    <div className="text-xs text-gray-400">
                      {formatTimeAgo(claim.timestamp)} ago
                    </div>
                    <div className="text-xs font-mono text-green-400">
                      ${parseFloat(claim.totalValueUSD).toFixed(2)}
                    </div>
                  </div>
                ))}
              </CardContent>
            </Card>
          </motion.div>
        )}
      </div>
    </div>
  );
}

client/src/components/spin-wheel-clean.t.content:
import { useState, useEffect, useCallback, useMemo } from "react";
import { motion } from "framer-motion";
import { useQueryClient } from "@tanstack/react-query";
import { useGameState } from "@/hooks/use-game-state";
import { useSimpleSpin } from "@/hooks/use-simple-spin";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";

interface SpinWheelProps {
  onSpinComplete?: (result: { segment: any, transactionHash?: string, isWin: boolean }) => void;
}

const wheelSegments = [
  { id: 'aidoge-1', name: 'AIDOGE', reward: '10000', color: '#3B82F6', tokenAddress: '0x287396E90c5febB4dC1EDbc0EEF8e5668cdb08D4' },
  { id: 'bankrupt-1', name: 'BUST', reward: '0', color: '#EF4444', tokenAddress: null },
  { id: 'boop-1', name: 'BOOP', reward: '20000', color: '#10B981', tokenAddress: '0x0E1CD6557D2BA59C61c75850E674C2AD73253952' },
  { id: 'bonus-1', name: 'BONUS', reward: '5000', color: '#F59E0B', tokenAddress: '0x0E1CD6557D2BA59C61c75850E674C2AD73253952' },
  { id: 'bobotrum-1', name: 'BOBOTRUM', reward: '15000', color: '#8B5CF6', tokenAddress: '0xaeA5bb4F5b5524dee0E3F931911c8F8df4576E19' },
  { id: 'bankrupt-2', name: 'BUST', reward: '0', color: '#EF4444', tokenAddress: null },
  { id: 'aidoge-2', name: 'AIDOGE', reward: '10000', color: '#3B82F6', tokenAddress: '0x287396E90c5febB4dC1EDbc0EEF8e5668cdb08D4' },
  { id: 'mega-1', name: 'JACKPOT', reward: '50000', color: '#F97316', tokenAddress: '0x287396E90c5febB4dC1EDbc0EEF8e5668cdb08D4' }
];

export default function SpinWheelClean(props: SpinWheelProps = {}) {
  const { onSpinComplete } = props;
  const [isSpinning, setIsSpinning] = useState(false);
  const [wheelRotation, setWheelRotation] = useState(0);
  const [landedSegment, setLandedSegment] = useState<number | null>(null);
  const { user } = useGameState();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { 
    isSpinning: isBlockchainSpinning, 
    triggerGasPopup,
    lastSpinResult,
    resetSpinResult,
    isConnected,
    userAddress
  } = useSimpleSpin();

  const segmentAngle = useMemo(() => 360 / wheelSegments.length, []);

  // Handle successful blockchain spin
  useEffect(() => {
    if (lastSpinResult && lastSpinResult.segment) {
      console.log('ðŸŽ¯ Processing spin result:', lastSpinResult);

      // Find the segment index
      const segmentIndex = wheelSegments.findIndex(seg => seg.name === lastSpinResult.segment.name);
      const targetSegmentIndex = segmentIndex >= 0 ? segmentIndex : 1; // Default to BUST if not found

      // Calculate rotation needed to land on target segment
      const targetAngle = -(targetSegmentIndex * segmentAngle);
      const spins = 5 + Math.random() * 3; // 5-8 full rotations
      const finalRotation = wheelRotation + (spins * 360) + targetAngle;

      console.log('ðŸ”„ Setting wheel rotation to:', finalRotation);
      setWheelRotation(finalRotation);
      setLandedSegment(targetSegmentIndex);

      // Stop the spinning state immediately and call completion callback
      setTimeout(() => {
        console.log('ðŸŽŠ Calling onSpinComplete callback');
        setIsSpinning(false);

        if (onSpinComplete) {
          onSpinComplete({
            segment: lastSpinResult.segment,
            transactionHash: lastSpinResult.transactionHash,
            isWin: lastSpinResult.isWin
          });
        }

        // Reset the spin result to prevent re-triggering
        resetSpinResult();

        // Delay query invalidation to prevent UI refresh from cancelling popup
        setTimeout(() => {
          queryClient.invalidateQueries({ queryKey: ["/api/user"] });
          queryClient.invalidateQueries({ queryKey: ["/api/stats"] });
        }, 2000);
      }, 3500); // Wait for wheel animation to complete
    }
  }, [lastSpinResult, wheelRotation, segmentAngle, queryClient, onSpinComplete, resetSpinResult]);

  // Simple handleSpin using new gas popup system
  const handleSpin = useCallback(async () => {
    console.log('ðŸŽ° handleSpin called', { 
      isSpinning, 
      isBlockchainSpinning, 
      user: !!user, 
      isConnected, 
      userAddress
    });

    if (isSpinning || isBlockchainSpinning || !user || !isConnected) {
      console.log('âŒ Spin blocked - requirements not met');
      return;
    }

    if (!userAddress) {
      toast({
        title: "Wallet Required",
        description: "Please connect your wallet to spin and pay gas fees.",
        variant: "destructive",
      });
      return;
    }

    // Check daily limit
    if ((user.spinsUsed || 0) >= 5) {
      toast({
        title: "Daily Limit Reached", 
        description: "You've used all your spins for today. Come back tomorrow!",
        variant: "destructive",
      });
      return;
    }

    setIsSpinning(true);
    setLandedSegment(null);

    // Reset any previous spin result
    resetSpinResult();

    try {
      console.log('Triggering gas popup...');
      const success = await triggerGasPopup();
      if (!success) {
        setIsSpinning(false);
      }
    } catch (error: any) {
      console.error('Spin error:', error);
      setIsSpinning(false);
    }
  }, [isSpinning, isBlockchainSpinning, user, isConnected, userAddress, toast, triggerGasPopup]);

  return (
    <div className="w-full mx-auto">


      {/* Wheel */}
      <div className="relative flex items-center justify-center mb-4">
        {/* Rotating Wheel */}
        <motion.div
          className="relative w-56 h-56 drop-shadow-2xl"
          animate={{ rotate: wheelRotation }}
          transition={{ 
            duration: isSpinning ? 3 : 0,
            ease: isSpinning ? [0.25, 0.46, 0.45, 0.94] : "linear"
          }}
        >
          <svg width="224" height="224" viewBox="0 0 224 224">
            <defs>
              <filter id="wheelShadow">
                <feDropShadow dx="0" dy="4" stdDeviation="6" floodOpacity="0.3"/>
              </filter>
              <radialGradient id="wheelGlow" cx="50%" cy="50%" r="50%">
                <stop offset="0%" stopColor="rgba(255,255,255,0.1)" />
                <stop offset="100%" stopColor="rgba(0,0,0,0.1)" />
              </radialGradient>
            </defs>

            {/* Outer ring for premium effect */}
            <circle cx="112" cy="112" r="105" fill="none" stroke="#fbbf24" strokeWidth="3" opacity="0.8"/>
            <circle cx="112" cy="112" r="102" fill="none" stroke="#ffffff" strokeWidth="1" opacity="0.6"/>
            {wheelSegments.map((segment, index) => {
              const startAngle = (index * segmentAngle - 90) * (Math.PI / 180);
              const endAngle = ((index + 1) * segmentAngle - 90) * (Math.PI / 180);
              const largeArc = segmentAngle > 180 ? 1 : 0;

              const x1 = 112 + 100 * Math.cos(startAngle);
              const y1 = 112 + 100 * Math.sin(startAngle);
              const x2 = 112 + 100 * Math.cos(endAngle);
              const y2 = 112 + 100 * Math.sin(endAngle);

              return (
                <g key={index}>
                  <path
                    d={`M 112 112 L ${x1} ${y1} A 100 100 0 ${largeArc} 1 ${x2} ${y2} Z`}
                    fill={segment.color}
                    stroke="white"
                    strokeWidth="2"
                    filter="url(#wheelShadow)"
                  />
                  {/* Subtle gradient overlay for depth */}
                  <path
                    d={`M 112 112 L ${x1} ${y1} A 100 100 0 ${largeArc} 1 ${x2} ${y2} Z`}
                    fill="url(#wheelGlow)"
                    opacity="0.3"
                  />

                  {(() => {
                    const midAngle = (startAngle + endAngle) / 2;
                    const textX = 112 + 60 * Math.cos(midAngle);
                    const textY = 112 + 60 * Math.sin(midAngle);

                    return (
                      <text
                        x={textX}
                        y={textY}
                        textAnchor="middle"
                        className="fill-white font-bold"
                        fontSize="10"
                      >
                        {segment.name}
                      </text>
                    );
                  })()}
                </g>
              );
            })}

            {/* Center Circle - only visual circle, no text */}
            <circle cx="112" cy="112" r="22" fill="#0f172a" stroke="#fbbf24" strokeWidth="3"/>
            <circle cx="112" cy="112" r="18" fill="#1e293b" stroke="white" strokeWidth="1"/>
          </svg>
        </motion.div>

        {/* Fixed Center Text (stays in place while wheel spins) */}
        <div className="absolute inset-0 flex items-center justify-center z-20">
          <div className="text-white font-bold text-xs bg-gradient-to-br from-slate-700 to-slate-900 rounded-full w-11 h-11 flex items-center justify-center border-2 border-amber-400 shadow-lg">
            SPIN
          </div>
        </div>

        {/* Fixed Premium Pointer (stays in place while wheel spins) */}
        <div className="absolute top-2 left-1/2 transform -translate-x-1/2 z-10">
          <svg width="30" height="30" viewBox="0 0 30 30">
            <defs>
              <linearGradient id="arrowGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="#fbbf24" />
                <stop offset="30%" stopColor="#ffffff" />
                <stop offset="70%" stopColor="#e5e7eb" />
                <stop offset="100%" stopColor="#9ca3af" />
              </linearGradient>
              <linearGradient id="arrowStroke" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="#d97706" />
                <stop offset="100%" stopColor="#374151" />
              </linearGradient>
              <filter id="arrowShadow">
                <feDropShadow dx="0" dy="2" stdDeviation="2" floodOpacity="0.5"/>
              </filter>
            </defs>
            {/* Main arrow pointing down into the wheel */}
            <polygon 
              points="15,5 22,18 8,18" 
              fill="url(#arrowGradient)" 
              stroke="url(#arrowStroke)" 
              strokeWidth="2"
              filter="url(#arrowShadow)"
            />
            {/* Inner highlight for 3D effect */}
            <polygon 
              points="15,7 20,16 10,16" 
              fill="rgba(255,255,255,0.4)" 
            />
            {/* Central ridge line for premium detail */}
            <line 
              x1="15" y1="7" 
              x2="15" y2="16" 
              stroke="rgba(255,255,255,0.7)" 
              strokeWidth="1"
            />
          </svg>
        </div>
      </div>


      {/* Spin Button */}
      <Button
        onClick={handleSpin}
        disabled={isSpinning || isBlockchainSpinning || !user || !isConnected}
        className="w-full h-10 bg-blue-600 hover:bg-blue-700 text-white font-bold text-base rounded-xl"
      >
        {isSpinning || isBlockchainSpinning ? "SPINNING..." : isConnected ? "SPIN WHEEL (Pay Gas)" : "CONNECT WALLET TO SPIN"}
      </Button>

      {!isSpinning && user && (
        <p className="text-xs text-gray-400 mt-2 text-center">
          {5 - (user.spinsUsed || 0)} spins remaining
        </p>
      )}
    </div>
  );
}

contracts/WheelGame.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract WheelGame is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    struct RewardToken {
        IERC20 token;
        uint256 rewardAmount;
        bool isActive;
    }

    struct PlayerStats {
        uint256 totalSpins;
        uint256 totalWins;
        uint256 lastSpinDate;
        uint256 dailySpins;
        mapping(address => uint256) pendingRewards;
    }

    // Token addresses on Arbitrum mainnet
    address public immutable AIDOGE = 0x09e18590e8f76b6cf471b3cd75fe1a1a9d2b2c2b;
    address public immutable BOOP = 0x13A7DeDb7169a17bE92B0E3C7C2315B46f4772B3;
    address public immutable BOBOTRUM = 0x60460971a3D79ef265dfafA393ffBCe97d91E8B8;

    // Game configuration
    uint256 public constant MAX_DAILY_SPINS = 5;
    uint256 public constant SECONDS_PER_DAY = 86400;

    // Reward tiers
    mapping(string => RewardToken) public rewards;
    mapping(address => PlayerStats) public players;

    // Game events
    event SpinResult(
        address indexed player,
        string segment,
        bool isWin,
        address tokenAddress,
        uint256 rewardAmount
    );

    event RewardsClaimed(
        address indexed player,
        address indexed token,
        uint256 amount
    );

    event RewardUpdated(string segment, uint256 newAmount);
    event ContractPaused(address indexed by);
    event ContractUnpaused(address indexed by);

    constructor() {
        // Initialize reward tiers with real token amounts
        rewards["JACKPOT"] = RewardToken({
            token: IERC20(AIDOGE),
            rewardAmount: 50000 * 10**18, // 50,000 AIDOGE
            isActive: true
        });

        rewards["AIDOGE"] = RewardToken({
            token: IERC20(AIDOGE),
            rewardAmount: 10000 * 10**18, // 10,000 AIDOGE
            isActive: true
        });

        rewards["BOOP"] = RewardToken({
            token: IERC20(BOOP),
            rewardAmount: 20000 * 10**18, // 20,000 BOOP
            isActive: true
        });

        rewards["BOBOTRUM"] = RewardToken({
            token: IERC20(BOBOTRUM),
            rewardAmount: 15000 * 10**18, // 15,000 BOBOTRUM
            isActive: true
        });

        rewards["BONUS"] = RewardToken({
            token: IERC20(BOOP),
            rewardAmount: 5000 * 10**18, // 5,000 BOOP
            isActive: true
        });

        rewards["BUST"] = RewardToken({
            token: IERC20(address(0)),
            rewardAmount: 0,
            isActive: false
        });
    }

    function spin(string memory segment) external nonReentrant whenNotPaused {
        PlayerStats storage player = players[msg.sender];

        // Check daily spin limit
        uint256 currentDay = block.timestamp / SECONDS_PER_DAY;
        uint256 lastSpinDay = player.lastSpinDate / SECONDS_PER_DAY;

        if (currentDay > lastSpinDay) {
            player.dailySpins = 0;
        }

        require(player.dailySpins < MAX_DAILY_SPINS, "Daily spin limit reached");

        // Update player stats
        player.totalSpins++;
        player.dailySpins++;
        player.lastSpinDate = block.timestamp;

        RewardToken memory reward = rewards[segment];
        bool isWin = reward.isActive && reward.rewardAmount > 0;

        if (isWin) {
            player.totalWins++;
            player.pendingRewards[address(reward.token)] += reward.rewardAmount;
        }

        emit SpinResult(
            msg.sender,
            segment,
            isWin,
            address(reward.token),
            reward.rewardAmount
        );
    }

    function claimRewards(address tokenAddress) external nonReentrant {
        PlayerStats storage player = players[msg.sender];
        uint256 amount = player.pendingRewards[tokenAddress];

        require(amount > 0, "No rewards to claim");

        player.pendingRewards[tokenAddress] = 0;

        IERC20(tokenAddress).safeTransfer(msg.sender, amount);

        emit RewardsClaimed(msg.sender, tokenAddress, amount);
    }

    function claimAllRewards() external nonReentrant {
        PlayerStats storage player = players[msg.sender];

        // Claim AIDOGE
        uint256 aidogeAmount = player.pendingRewards[AIDOGE];
        if (aidogeAmount > 0) {
            player.pendingRewards[AIDOGE] = 0;
            IERC20(AIDOGE).safeTransfer(msg.sender, aidogeAmount);
            emit RewardsClaimed(msg.sender, AIDOGE, aidogeAmount);
        }

        // Claim BOOP
        uint256 boopAmount = player.pendingRewards[BOOP];
        if (boopAmount > 0) {
            player.pendingRewards[BOOP] = 0;
            IERC20(BOOP).safeTransfer(msg.sender, boopAmount);
            emit RewardsClaimed(msg.sender, BOOP, boopAmount);
        }

        // Claim BOBOTRUM
        uint256 bobotrumAmount = player.pendingRewards[BOBOTRUM];
        if (bobotrumAmount > 0) {
            player.pendingRewards[BOBOTRUM] = 0;
            IERC20(BOBOTRUM).safeTransfer(msg.sender, bobotrumAmount);
            emit RewardsClaimed(msg.sender, BOBOTRUM, bobotrumAmount);
        }
    }

    function getPlayerStats(address playerAddress) external view returns (
        uint256 totalSpins,
        uint256 totalWins,
        uint256 lastSpinDate,
        uint256 dailySpins,
        uint256 spinsRemaining
    ) {
        PlayerStats storage player = players[playerAddress];

        uint256 currentDay = block.timestamp / SECONDS_PER_DAY;
        uint256 lastSpinDay = player.lastSpinDate / SECONDS_PER_DAY;

        uint256 currentDailySpins = currentDay > lastSpinDay ? 0 : player.dailySpins;

        return (
            player.totalSpins,
            player.totalWins,
            player.lastSpinDate,
            currentDailySpins,
            MAX_DAILY_SPINS - currentDailySpins
        );
    }

    function getPendingRewards(address playerAddress) external view returns (
        uint256 aidogeRewards,
        uint256 boopRewards,
        uint256 bobotrumRewards
    ) {
        PlayerStats storage player = players[playerAddress];

        return (
            player.pendingRewards[AIDOGE],
            player.pendingRewards[BOOP],
            player.pendingRewards[BOBOTRUM]
        );
    }

    // Admin functions
    function updateRewardAmount(string memory segment, uint256 newAmount) external onlyOwner {
        rewards[segment].rewardAmount = newAmount;
        emit RewardUpdated(segment, newAmount);
    }

    function emergencyWithdraw(address tokenAddress) external onlyOwner {
        require(tokenAddress != address(0), "Invalid token address");
        IERC20 token = IERC20(tokenAddress);
        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(owner(), balance);
    }

    function pause() external onlyOwner {
        _pause();
        emit ContractPaused(msg.sender);
    }

    function unpause() external onlyOwner {
        _unpause();
        emit ContractUnpaused(msg.sender);
    }
}

