      // Check if wallet has any tokens
      if (process.env.WALLET_PRIVATE_KEY) {
        let privateKey = process.env.WALLET_PRIVATE_KEY.trim();
        if (!privateKey.startsWith('0x')) {
          privateKey = '0x' + privateKey;
        }

        const wallet = new ethers.Wallet(privateKey, provider);
        const balance = await contract.balanceOf(wallet.address);
        console.log(`ðŸ¦ Your balance: ${ethers.formatUnits(balance, decimals)} ${symbol}`);

        if (balance === 0n) {
          console.log(`ðŸ’¡ Get tokens by calling: faucet(${ethers.parseUnits("1000", decimals)})`);
