const { ethers } = require('ethers');
require('dotenv').config();

async function deployContracts() {
    console.log('ðŸš€ Deploying fresh contracts to Arbitrum Sepolia...');

    const privateKey = process.env.WALLET_PRIVATE_KEY;
    if (!privateKey) {
        throw new Error('âŒ WALLET_PRIVATE_KEY not set');
    }

    // Setup provider and wallet
    const provider = new ethers.JsonRpcProvider('https://sepolia-rollup.arbitrum.io/rpc');
    const wallet = new ethers.Wallet(privateKey, provider);

    console.log('ðŸ“ Deploying with account:', wallet.address);

    // Check balance
    const balance = await wallet.getBalance();
    console.log('ðŸ’° Balance:', ethers.formatEther(balance), 'ETH');

    if (balance < ethers.parseEther('0.001')) {
        throw new Error('âŒ Insufficient balance for deployment');
    }

    // Simple Test Token Contract
    const testTokenSource = `
        // SPDX-License-Identifier: MIT
        pragma solidity ^0.8.19;

        contract TestToken {
            string public name;
            string public symbol;
            uint8 public decimals;
            uint256 public totalSupply;

            mapping(address => uint256) public balanceOf;
            mapping(address => mapping(address => uint256)) public allowance;

            event Transfer(address indexed from, address indexed to, uint256 value);
            event Approval(address indexed owner, address indexed spender, uint256 value);

            constructor(string memory _name, string memory _symbol, uint8 _decimals, uint256 _totalSupply) {
                name = _name;
                symbol = _symbol;
                decimals = _decimals;
                totalSupply = _totalSupply * 10**_decimals;
                balanceOf[msg.sender] = totalSupply;
                emit Transfer(address(0), msg.sender, totalSupply);
            }

            function transfer(address to, uint256 value) public returns (bool) {
                require(balanceOf[msg.sender] >= value, "Insufficient balance");
                balanceOf[msg.sender] -= value;
                balanceOf[to] += value;
                emit Transfer(msg.sender, to, value);
                return true;
            }

            function approve(address spender, uint256 value) public returns (bool) {
                allowance[msg.sender][spender] = value;
                emit Approval(msg.sender, spender, value);
                return true;
            }

            function transferFrom(address from, address to, uint256 value) public returns (bool) {
                require(balanceOf[from] >= value, "Insufficient balance");
                require(allowance[from][msg.sender] >= value, "Allowance exceeded");
                balanceOf[from] -= value;
                balanceOf[to] += value;
                allowance[from][msg.sender] -= value;
                emit Transfer(from, to, value);
                return true;
            }
        }
    `;

    // Simple Wheel Game Contract
    const wheelGameSource = `
        // SPDX-License-Identifier: MIT
        pragma solidity ^0.8.19;

        contract SimpleWheelGame {
            address public owner;
